import React, { 
    createContext, 
    useState, 
    useEffect 
} from "react";
import { getUserInfo } from "../../db/Repository";
export const UserContext = createContext()

export const UserProvider = ({children}) => {
    const [user, setUser] = useState(null)
    const [token, setToken] = useState(null);

    useEffect(()=> {
        if(token) {
            getUserInfo()
            .then(res => {
                setUser(
                    {
                        fname: res.fname,
                        lname: res.lname, 
                        img: res.img,
                        email: res.email
                    }
                )
            })
            .catch(err => {
                console.log(err)
            })
        }
      }, [token])

    return (
        <UserContext.Provider value={
            {
                user,
                setUser,
                token,
                setToken
            }
        }>
            {children}
        </UserContext.Provider>
    )

}
